import React from "react";
import "./styles.css";

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: "0", pi: 0};
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
    console.log()
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log(this.state.value);
    //this.calculatePi(this.state.value);
  }

  calculatePi(n) {
    console.log(n)
    n = parseInt(n,10);
    let numPointsInCircle = 0;
    let numPointsTotal = 0;
    let points = [];
    let x = 0;
    let y = 0;
    for (var i = 0; i < n; i++) {
      x = Math.random();
      y = Math.random();
      points.push((x, y));
      if (x ** 2 + y ** 2 <= 1) {
        numPointsInCircle++;
      }
      numPointsTotal++;
    }
    let pi = 4 * numPointsInCircle / numPointsTotal;
    this.setState({ pi: pi });
  }

  render() {
    return (
      <div>
        <h1>Hello {this.props.name}</h1>
        <form onSubmit={this.calculatePi}>
          <label>
            Name:
            <input
              type="text"
              value={this.state.value}
              onChange={this.handleChange}
            />
          </label>
          <input type="submit" value="Submit" />
        </form>
        <h2>Value of Pi: {this.state.pi}</h2>
      </div>
    );
  }
}
