import React, { Component } from 'react';
import { Circle, Layer, Rect, Text } from 'react-konva';
import Konva from 'konva';

class ColoredRect extends React.Component {
  state = {
    color: 'green',
    square: 750,
    radius: 50
  };
  handleClick = () => {
    this.setState({
      color: Konva.Util.getRandomColor()
    });
  };
  render() {
    return (
      <Layer>
      <Rect
        x={20}
        y={20}
        width={this.state.square}
        stroke="black"
        height={this.state.square}
        onClick={this.handleClick}
      />
      <Circle x={(this.state.square/2)+20} y={20+this.state.radius} radius={this.state.radius} stroke="black"/>
      </Layer>
    );
  }
}

export default ColoredRect;
